import EventCover from './components/EventCover.vue';
import EventTitle from './components/EventTitle.vue';
export default {
  components: {
    EventCover,
    EventTitle
  },
  computed: {
    event() {
      return this.events[this.currentEvent];
    }
  },
  data() {
    return {
      events: [],
      currentEvent: 0
    };
  },
  created() {
    this.getEvents();
  },
  methods: {
    async getEvents() {
      let res = await fetch('events.json');
      let data = await res.json();
      this.events = data;
    }
  }
};
